  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'experiment1',
	title: 'Standard Search vs. Stemming',
	sidebar_label: 'Stemming'
};
export const metadata = {
  "id": "experiments/experiment1",
  "isDocsHomePage": false,
  "title": "Standard Search vs. Stemming",
  "description": "For our first experiment we connected a Google CoLab Notebook to our Elasticsearch instance and started comparing different build-in search metrics of Elasticsearch.",
  "source": "@site/docs/experiments/experiment1.md",
  "permalink": "/docs/experiments/experiment1",
  "editUrl": "https://github.com/pragmalingu/pragmalingu/docs/experiments/experiment1.md",
